mapper.select:
select

from teacher where age >= #{ageLeft}
ORDER BY id ASC LIMIT #{_skiprows}, #{_pagesize}

select

from teacher where age >= #{ageLeft} and age <= #{ageRight}
ORDER BY id ASC LIMIT #{_skiprows}, #{_pagesize}

select

from teacher where id >= #{idLeft} and id <= #{idRight}
ORDER BY id ASC LIMIT #{_skiprows}, #{_pagesize}
