mapper.select:
select

from user where age >= #{ageLeft}
ORDER BY id ASC LIMIT #{_skiprows}, #{_pagesize}

select

from user where age >= #{ageLeft} and age <= #{ageRight}
ORDER BY id ASC LIMIT #{_skiprows}, #{_pagesize}

select

from user where id >= #{idLeft} and id <= #{idRight}
ORDER BY id ASC LIMIT #{_skiprows}, #{_pagesize}
